#include <stdio.h>
/****************************************************************
int main(void) {
    int *p;     //p是个变量名字，int * 表示该p变量只能存储int类型变量的地址
    int i = 10, j;
    //*p = i;
    p = &i;
    *p = 20;
    printf("*p = %d\ni = %d\nj = %d\n", *p, i, j);
}
****************************************************************/
/****************************************************************
//函数形参改变实参的值
void f(int * p){
    //不是定义了一个名字叫做*p的形参，而是定义了一个形参，该形参名字叫做p，它的类型是int *
    *p = 20;
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
int main(void) {
    int i = 9;
    f(&i);
    printf("i = %d\n", i);
}
****************************************************************/